    static ArrayList<RumahAdat> getListData() {
        ArrayList<RumahAdat> list = new ArrayList<>();
        for (int posisi = 0; posisi < namaRumah.length; posisi++) {
            RumahAdat rumahadat = new RumahAdat();
            rumahadat.setNama(namaRumah[posisi]);
            rumahadat.setDetail(detailRumah[posisi]);
            rumahadat.setGambar(gambarRumah[posisi]);
            rumahadat.setProvinsi(namaProv[posisi]);
            list.add(rumahadat);
        }
        return list;
    }
